package com.nology.ECF;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@CrossOriginpublic class ArticleCardController {    @Autowired    ArticleRepository articleRepository;    @ExceptionHandler    public ResponseEntity<String> handleException(Exception exception){        return ResponseEntity.status(HttpStatus.NOT_FOUND).body(exception.getMessage());    }    @GetMapping("/article/{id}")    public ResponseEntity<ArticleCard> getArticleById(@PathVariable String id){        ArticleCard articleCard = articleRepository.getArticlesById(id);        return ResponseEntity.status(HttpStatus.FOUND).body(articleCard);    }    @PostMapping("/article")    public ResponseEntity<ArticleCard> createArticleCard(@RequestBody ArticleCard article){        articleRepository.addArticle(article);        return ResponseEntity.status(HttpStatus.FOUND).body(article);    }}