package com.nology.ECF;import org.springframework.stereotype.Repository;import java.util.ArrayList;import java.util.List;@Repositorypublic class ArticleRepository {    private final List<ArticleCard> articles = new ArrayList<>();    public void addArticle(ArticleCard article){        articles.add(article);    }    public ArticleCard getArticlesById(String id){        for (ArticleCard article: articles) {            if (article.getId().equals(id)) {                return article;            }        }        throw new ArticleNotFoundException();    }    public List<ArticleCard> getAllArticles() {        return articles;    }    public void deleteArticleById(String id){        for (int i = 0; i < articles.size() ; i++) {            if (articles.get(i).getId().equals(id)) {                articles.remove(i);                return;            }        }        throw new ArticleNotFoundException();    }}