package com.nology.ECF;public class Challenge {    private String type;    private String title;    private int duration;    private String id;    public Challenge(String type, String title, int duration, String id) {        this.type = type;        this.title = title;        this.duration = duration;        this.id = id;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public int getDuration() {        return duration;    }    public void setDuration(int duration) {        this.duration = duration;    }}