package com.nology.ECF;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@CrossOriginpublic class ChallengeController {    @Autowired    ChallengeRepository challengeRepository;    @ExceptionHandler    public ResponseEntity<String> handleException(Exception exception){        return ResponseEntity.status(HttpStatus.NOT_FOUND).body(exception.getMessage());    }    @GetMapping("/challenge/{id}")    public ResponseEntity<Challenge> getChallengeById(@PathVariable String id){        Challenge challenge = challengeRepository.getChallengeById(id);        return ResponseEntity.status(HttpStatus.FOUND).body(challenge);    }    @PostMapping("/challenge")    public ResponseEntity<Challenge> createArticleCard(@RequestBody Challenge challenge){        challengeRepository.addChallenge(challenge);        return ResponseEntity.status(HttpStatus.FOUND).body(challenge);    }}