package com.nology.ECF;import org.springframework.stereotype.Repository;import javax.persistence.Entity;import java.util.ArrayList;import java.util.List;@Repositorypublic class ChallengeRepository {    private final List<Challenge> challenges = new ArrayList<>();    public void addChallenge(Challenge challenge){        challenges.add(challenge);    }    public Challenge getChallengeById(String id){        for (Challenge challenge: challenges) {            if (challenge.getId().equals(id)) {                return challenge;            }        }        throw new ChallengeNotFoundException();    }    public List<Challenge> getAllChallenges() {        return challenges;    }    public void deleteChallengeById(String id){        for (int i = 0; i < challenges.size() ; i++) {            if (challenges.get(i).getId().equals(id)) {                challenges.remove(i);                return;            }        }        throw new ChallengeNotFoundException();    }}