package com.nology.ECF;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@CrossOriginpublic class LessonController {    @Autowired    LessonRepository lessonRepository;    @ExceptionHandler    public ResponseEntity<String> handleException(Exception exception){        return ResponseEntity.status(HttpStatus.NOT_FOUND).body(exception.getMessage());    }    @GetMapping("/lesson/{id}")    public ResponseEntity<Lesson> getLessonById(@PathVariable String id){        Lesson lesson = lessonRepository.getLessonById(id);        return ResponseEntity.status(HttpStatus.FOUND).body(lesson);    }    @PostMapping("/lesson")    public ResponseEntity<Lesson> createArticleCard(@RequestBody Lesson lesson){        lessonRepository.addLesson(lesson);        return ResponseEntity.status(HttpStatus.FOUND).body(lesson);    }}