package com.nology.ECF;import org.springframework.stereotype.Repository;import java.util.ArrayList;import java.util.List;@Repositorypublic class LessonRepository {    private final List<Lesson> lessons = new ArrayList<>();    public void addLesson(Lesson lesson){        lessons.add(lesson);    }    public Lesson getLessonById(String id){        for (Lesson lesson: lessons) {            if (lesson.getId().equals(id)) {                return lesson;            }        }        throw new LessonNotFoundException();    }    public List<Lesson> getAllLessons() {        return lessons;    }    public void deleteLessonById(String id){        for (int i = 0; i < lessons.size() ; i++) {            if (lessons.get(i).getId().equals(id)) {                lessons.remove(i);                return;            }        }        throw new LessonNotFoundException();    }}