package com.nology.ECF;public class MarketItem {    private String imageUrl;    private String heading;    private String overview;    private String id;    private int skillPoints;    private String[] trophies;    private boolean isLocked;    public MarketItem(String imageUrl, String heading, String overview, String id, int skillPoints, String[] trophies, boolean isLocked) {        this.imageUrl = imageUrl;        this.heading = heading;        this.overview = overview;        this.id = id;        this.skillPoints = skillPoints;        this.trophies = trophies;        this.isLocked = isLocked;    }    public String getImageUrl() {        return imageUrl;    }    public void setImageUrl(String imageUrl) {        this.imageUrl = imageUrl;    }    public String getHeading() {        return heading;    }    public void setHeading(String heading) {        this.heading = heading;    }    public String getOverview() {        return overview;    }    public void setOverview(String overview) {        this.overview = overview;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public int getSkillPoints() {        return skillPoints;    }    public void setSkillPoints(int skillPoints) {        this.skillPoints = skillPoints;    }    public String[] getTrophies() {        return trophies;    }    public void setTrophies(String[] trophies) {        this.trophies = trophies;    }    public boolean isLocked() {        return isLocked;    }    public void setLocked(boolean locked) {        isLocked = locked;    }}