package com.nology.ECF;import org.springframework.stereotype.Repository;import java.util.ArrayList;import java.util.List;@Repositorypublic class MarketItemRepository {    private final List<MarketItem> marketItems = new ArrayList<>();    public void addMarketItem(MarketItem marketItem){        marketItems.add(marketItem);    }    public MarketItem getMarketItemById(String id){        for (MarketItem marketItem: marketItems) {            if (marketItem.getId().equals(id)) {                return marketItem;            }        }        throw new MarketItemNotFoundException();    }    public List<MarketItem> getAllMarketItems() {        return marketItems;    }    public void deleteMarketItemById(String id){        for (int i = 0; i < marketItems.size() ; i++) {            if (marketItems.get(i).getId().equals(id)) {                marketItems.remove(i);                return;            }        }        throw new MarketItemNotFoundException();    }}