package com.nology.ECF.service;import com.google.api.core.ApiFuture;import com.google.cloud.firestore.DocumentReference;import com.google.cloud.firestore.DocumentSnapshot;import com.google.cloud.firestore.Firestore;import com.google.cloud.firestore.WriteResult;import com.google.firebase.cloud.FirestoreClient;import com.nology.ECF.ArticleCard;import org.springframework.stereotype.Service;import java.util.concurrent.ExecutionException;@Servicepublic class ArticleCardService {    private static final String COLLECTION_NAME = "articleCard";    public String saveArticleCard(ArticleCard articleCard) throws ExecutionException, InterruptedException {        Firestore dbFirestore = FirestoreClient.getFirestore();        ApiFuture<WriteResult> collectionApiFuture =  dbFirestore.collection(COLLECTION_NAME).document(articleCard.getTitle()).set(articleCard);        return collectionApiFuture.get().getUpdateTime().toString();    }    public ArticleCard getArticleDetails(String title) throws ExecutionException, InterruptedException {        Firestore dbFirestore = FirestoreClient.getFirestore();        DocumentReference documentReference = dbFirestore.collection(COLLECTION_NAME).document(title);        ApiFuture<DocumentSnapshot> future = documentReference.get();        DocumentSnapshot document = future.get();        ArticleCard article = null;        if (document.exists()) {            article = document.toObject(ArticleCard.class);                    }        return article;    }    }